package com.irof.util;import android.view.MotionEvent;import android.annotation.SuppressLint;@SuppressLint("NewApi")public class MouseUtil {	public static int getPointerCount(MotionEvent event) {		return event.getPointerCount();	}	public static float getX(MotionEvent event, int p) {		return event.getX(p);	}	public static float getY(MotionEvent event, int p) {		return event.getY(p);	}}